//从底部弹框
var RssAdvert = {}
RssAdvert.PageSlide = function (placeid, wrap) {
    new Ajax("advert/list").keyvalue("placeid", placeid).getJson(function (json) {
        var html = "", point = "";
        $.each(json, function (idx, rows) {
            html += '<li><img src="' + RssApp.UpHost + rows["ico"] + '" /></li>';
            point += '<li></li>';
        });
        wrap.find("ul").html(html).pageslide();

        wrap.find("ol").html(point).find("li").first().addClass("current");
    });
}

$("[rsspageslide]").each(function () {
    var t = $(this);
    RssAdvert.PageSlide(t.attr("rsspageslide"), t);
});