//录制
var RssRecord = {}
RssRecord.Params = {}
RssRecord.Time = 60;
RssRecord.Adapter = "";
RssRecord.Action = "";

//参数说明：
RssRecord.Submit = function (params) {
    RssAppAdapter($.extend(null, { "adapter": this.Adapter, "action": this.Action }, this.Params, params || {}));
}
RssRecord.Upload = function (apiurl) {
    this.Params = { "url": apiurl || RssApp.UpFileApi };
    return this.Submit("upload");
}
RssRecord.SetTime = function (time) {
    this.Time = time || 60;
    return this;
}
RssRecord.Audio = function () {
    this.Adapter = "RecordAudio";
    return this;
}

RssRecord.Video = function () {
    this.Adapter = "RecordVideo";
    return this;
}
RssRecord.Launch = function () {
    this.Action = "launch";
    return this.Submit();
};
RssRecord.Start = function (time) {
    this.Time = time || this.Time;
    this.Action = "start";
    return this.Submit({ "time": this.Time });
};
RssRecord.Stop = function () {
    this.Action = "stop";
    return this.Submit();
};
RssRecord.Play = function () {

}
RssRecord.onUpload = function (json) {

}
RssRecord.onProgress = function (json) {

}
RssRecord.onStart = function (json) {

}
RssRecord.onPause = function (json) {

}
RssRecord.onStop = function (json) {

}
RssRecord.onPlay = function (json) {

}
RssRecord.onError = function (json) {

}