/// <reference path="jquery.min.js" />
/// <reference path="rsseasy.min.js" />

//进度条
function RssProgBar(bar) {
    this.bar = bar.constructor == String ? $("#" + bar) : bar;
    this.tick = "0%";
}

RssProgBar.prototype.Progress = function (max, progress) {
    this.tick = parseInt(parseInt(progress) / parseInt(max) * 100) + "%"
    this.bar.width(this.tick);
    return this;
}
RssProgBar.prototype.Html = function () {
    this.bar.html(this.tick);
    return this;
}
RssProgBar.prototype.Position = function (max, progress) {
    this.tick = parseInt(parseInt(progress) / parseInt(max) * 100) + "%"
    this.bar.css({ "left": this.tick });
    return this;
}